#include <bits/stdc++.h>

using namespace std;


int hashish(string s, int start, int end){
    int sum=0;
    for(int i=start; i<=end; i++){
        int temp = s[i]-'a';
        temp = (temp+2)*(temp+1)- (temp*2) + (temp*9) + (temp*117);
        sum += temp;
    }
    return sum;
}
checkInclusion(string s1, string s2) {
    int lens1=s1.length();
    int lens2=s2.length();
    int key = hashish(s1, 0, lens1-1);
    for(int i=0; i<=lens2-lens1; i++){
        int x= hashish(s2, i, i+lens1-1);
        if(x == key){
            cout<<i<<" "<<key<<" "<<x<<"\n";
            return true;
        }
    }
    return false;
}

int main(){
    string p = "xuumbjffxuzovdwrnolopeingppzgorotzdqfprokkmucxwsub";
    string s = "";
    checkInclusion(p, s);

    p = "snvrcpaywg";
    s = "";
    checkInclusion(p, s);
}
